                if (value == 0) break;
            }
            return buffer;
        }
    }

    /**
     * @dev Converts a `int256` to its ASCII `string` decimal representation.
     */
    function toString(int256 value) internal pure returns (string memory) {
        return string(abi.encodePacked(value < 0 ? "-" : "", toString(SignedMath.abs(value))));
    }

    /**
     * @dev Converts a `uint256` to its ASCII `string` hexadecimal representation.
     */
    function toHexString(uint256 value) internal pure returns (string memory) {
        unchecked {
            return toHexString(value, Math.log256(value) + 1);
        }
    }

    /**
     * @dev Converts a `uint256` to its ASCII `string` hexadecimal representation with fixed length.
     */
    function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
        bytes memory buffer = new bytes(2 * length + 2);
        buffer[0] = "0";
        buffer[1] = "x";
        for (uint256 i = 2 * length + 1; i > 1; --i) {
            buffer[i] = _SYMBOLS[value & 0xf];
            value >>= 4;
        }
        require(value == 0, "Strings: hex length insufficient");
        return string(buffer);
    }

    /**
     * @dev Converts an `address` with fixed length of 20 bytes to its not checksummed ASCII `string` hexadecimal representation.
     */
    function toHexString(address addr) internal pure returns (string memory) {
        return toHexString(uint256(uint160(addr)), _ADDRESS_LENGTH);
    }

    /**
     * @dev Returns true if the two strings are equal.
     */
    function equal(string memory a, string memory b) internal pure returns (bool) {
        return keccak256(bytes(a)) == keccak256(bytes(b));
    }
}

contracts_src/CommitmentStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title CommitmentStaking
 * @dev Smart contract for commitment-based staking with accountability pods
 * Users stake tokens to commit to learning streaks/goals. Backend signs attestations
 * for milestone completion. Successful commitments earn rewards, failed ones go to scholarship pool.
 */
contract CommitmentStaking is Ownable, ReentrancyGuard, Pausable {

    // ============ Structs ============

    struct Commitment {
        address user;
        uint256 stakeAmount;
        uint256 targetValue;        // e.g., 7 for 7-day streak
        uint256 currentProgress;    // Current progress toward target
        uint256 startTime;
        uint256 deadline;
        uint256 podId;              // 0 for individual, >0 for pod commitments
        bool isActive;
        bool isCompleted;
        bool isClaimed;
    }

    struct Pod {
        string name;
        uint256 stakeAmount;        // Required stake per member
        uint256 targetValue;
        uint256 deadline;
        address[] members;
        uint256 successfulMembers;
        uint256 failedMembers;
        bool isActive;
        bool isStarted;
    }

    // ============ State Variables ============

    // Commitment tracking
    mapping(uint256 => Commitment) public commitments;
    uint256 public commitmentCounter;

    // Pod tracking
    mapping(uint256 => Pod) public pods;
    uint256 public podCounter;
    mapping(address => uint256[]) public userCommitments;
    mapping(uint256 => mapping(address => bool)) public podMemberships;

    // Attestation authority (backend signer)
    address public attestationAuthority;
    mapping(bytes32 => bool) public usedAttestations;

    // Scholarship pool
    address public scholarshipPool;
    uint256 public totalScholarshipFunds;

    // Reward settings
    uint256 public rewardMultiplier = 110; // 110% = 10% bonus on success
    uint256 public constant BASIS_POINTS = 100;

    // ============ Events ============

    event CommitmentCreated(
        uint256 indexed commitmentId,
        address indexed user,
        uint256 stakeAmount,
        uint256 targetValue,
        uint256 deadline,
        uint256 podId
    );

    event CommitmentCompleted(
        uint256 indexed commitmentId,
        address indexed user,
        uint256 progress
    );

    event CommitmentClaimed(
        uint256 indexed commitmentId,
        address indexed user,
        uint256 rewardAmount
    );

    event CommitmentFailed(
        uint256 indexed commitmentId,
        address indexed user,
        uint256 penaltyAmount
    );

    event PodCreated(
        uint256 indexed podId,
        string name,
        uint256 stakeAmount,
        uint256 targetValue,
        uint256 deadline
    );

    event PodMemberJoined(
        uint256 indexed podId,
        address indexed member,
        uint256 commitmentId
    );

    event ScholarshipDistributed(
        address indexed recipient,
        uint256 amount
    );

    event ProgressUpdated(
        uint256 indexed commitmentId,
        uint256 progress
    );

    // ============ Constructor ============

    constructor(
        address _attestationAuthority,
        address _scholarshipPool
    ) {
        require(_attestationAuthority != address(0), "Invalid attestation authority");
        require(_scholarshipPool != address(0), "Invalid scholarship pool");

        attestationAuthority = _attestationAuthority;
        scholarshipPool = _scholarshipPool;
    }

    // ============ Commitment Functions ============

    /**
     * @dev Create a new individual commitment
     */
    function createCommitment(
        uint256 _targetValue,
        uint256 _durationDays
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(msg.value > 0, "Stake amount must be greater than 0");
        require(_targetValue > 0, "Target value must be greater than 0");
        require(_durationDays > 0, "Duration must be greater than 0");
        require(msg.value >= 0.01 ether && msg.value <= 1 ether, "Stake must be between 0.01 and 1.0");

        uint256 commitmentId = commitmentCounter++;
        uint256 deadline = block.timestamp + (_durationDays * 1 days);

        commitments[commitmentId] = Commitment({
            user: msg.sender,
            stakeAmount: msg.value,
            targetValue: _targetValue,
            currentProgress: 0,
            startTime: block.timestamp,
            deadline: deadline,
            podId: 0,
            isActive: true,
            isCompleted: false,
            isClaimed: false
        });

        userCommitments[msg.sender].push(commitmentId);

        emit CommitmentCreated(
            commitmentId,
            msg.sender,
            msg.value,
            _targetValue,
            deadline,
            0
        );

        return commitmentId;
    }

    /**
     * @dev Update commitment progress with backend attestation
     */
    function updateProgress(
        uint256 _commitmentId,
        uint256 _progress,
        bytes32 _attestationHash,
        bytes memory _signature
    ) external nonReentrant {
        Commitment storage commitment = commitments[_commitmentId];

        require(commitment.isActive, "Commitment not active");
        require(!commitment.isCompleted, "Commitment already completed");
        require(block.timestamp <= commitment.deadline, "Commitment deadline passed");
        require(!usedAttestations[_attestationHash], "Attestation already used");
        require(_progress <= commitment.targetValue, "Progress cannot exceed target");

        // Verify attestation signature
        bytes32 messageHash = keccak256(abi.encodePacked(
            _commitmentId,
            commitment.user,
            _progress,
            _attestationHash
        ));

        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);
        require(recoverSigner(ethSignedMessageHash, _signature) == attestationAuthority, "Invalid attestation");

        // Mark attestation as used
        usedAttestations[_attestationHash] = true;

        // Update progress
        commitment.currentProgress = _progress;

        // Check if commitment is completed
        if (_progress >= commitment.targetValue) {
            commitment.isCompleted = true;
            emit CommitmentCompleted(_commitmentId, commitment.user, _progress);

            // Update pod stats if part of a pod
            if (commitment.podId > 0) {
                pods[commitment.podId].successfulMembers++;
            }
        }

        emit ProgressUpdated(_commitmentId, _progress);
    }

    /**
     * @dev Claim rewards for completed commitment
     */
    function claimReward(uint256 _commitmentId) external nonReentrant {
        Commitment storage commitment = commitments[_commitmentId];

        require(msg.sender == commitment.user, "Not commitment owner");
        require(commitment.isCompleted, "Commitment not completed");
        require(!commitment.isClaimed, "Already claimed");
        require(commitment.isActive, "Commitment not active");

        commitment.isClaimed = true;
        commitment.isActive = false;

        // Calculate reward (stake + bonus)
        uint256 reward = (commitment.stakeAmount * rewardMultiplier) / BASIS_POINTS;

        // Transfer reward
        (bool success, ) = msg.sender.call{value: reward}("");
        require(success, "Reward transfer failed");

        emit CommitmentClaimed(_commitmentId, msg.sender, reward);
    }

    /**
     * @dev Mark commitment as failed and send stake to scholarship pool
     */
    function failCommitment(uint256 _commitmentId) external {
        Commitment storage commitment = commitments[_commitmentId];

        require(
            msg.sender == commitment.user || msg.sender == owner(),
            "Not authorized"
        );
        require(commitment.isActive, "Commitment not active");
        require(!commitment.isCompleted, "Commitment already completed");
        require(block.timestamp > commitment.deadline, "Deadline not passed");
        require(!commitment.isClaimed, "Already claimed");

        commitment.isActive = false;

        // Send stake to scholarship pool
        uint256 penalty = commitment.stakeAmount;
        totalScholarshipFunds += penalty;

        (bool success, ) = scholarshipPool.call{value: penalty}("");
        require(success, "Scholarship transfer failed");

        // Update pod stats if part of a pod
        if (commitment.podId > 0) {
            pods[commitment.podId].failedMembers++;
        }

        emit CommitmentFailed(_commitmentId, commitment.user, penalty);
    }

    // ============ Pod Functions ============

    /**
     * @dev Create a new accountability pod
     */
    function createPod(
        string memory _name,
        uint256 _stakeAmount,
        uint256 _targetValue,
        uint256 _durationDays
    ) external whenNotPaused returns (uint256) {
        require(_stakeAmount >= 0.01 ether && _stakeAmount <= 1 ether, "Invalid stake amount");
        require(_targetValue > 0, "Target value must be greater than 0");
        require(_durationDays > 0, "Duration must be greater than 0");

        uint256 podId = podCounter++;
        uint256 deadline = block.timestamp + (_durationDays * 1 days);

        pods[podId] = Pod({
            name: _name,
            stakeAmount: _stakeAmount,
            targetValue: _targetValue,
            deadline: deadline,
            members: new address[](0),
            successfulMembers: 0,
            failedMembers: 0,
            isActive: true,
            isStarted: false
        });

        emit PodCreated(podId, _name, _stakeAmount, _targetValue, deadline);

        return podId;
    }

    /**
     * @dev Join an accountability pod
     */
    function joinPod(uint256 _podId) external payable nonReentrant whenNotPaused returns (uint256) {
        Pod storage pod = pods[_podId];

        require(pod.isActive, "Pod not active");
        require(!pod.isStarted, "Pod already started");
        require(msg.value == pod.stakeAmount, "Incorrect stake amount");
        require(!podMemberships[_podId][msg.sender], "Already in pod");

        // Create commitment for pod member
        uint256 commitmentId = commitmentCounter++;

        commitments[commitmentId] = Commitment({
            user: msg.sender,
            stakeAmount: msg.value,
            targetValue: pod.targetValue,
            currentProgress: 0,
            startTime: block.timestamp,
            deadline: pod.deadline,
            podId: _podId,
            isActive: true,
            isCompleted: false,
            isClaimed: false
        });

        // Update pod
        pod.members.push(msg.sender);
        podMemberships[_podId][msg.sender] = true;
        userCommitments[msg.sender].push(commitmentId);

        emit PodMemberJoined(_podId, msg.sender, commitmentId);
        emit CommitmentCreated(
            commitmentId,
            msg.sender,
            msg.value,
            pod.targetValue,
            pod.deadline,
            _podId
        );

        return commitmentId;
    }

    /**
     * @dev Start a pod (locks it from new members)
     */
    function startPod(uint256 _podId) external {
        Pod storage pod = pods[_podId];
        require(pod.isActive, "Pod not active");
        require(!pod.isStarted, "Pod already started");
        require(pod.members.length >= 2, "Need at least 2 members");

        pod.isStarted = true;
    }

    // ============ Admin Functions ============

    /**
     * @dev Update attestation authority
     */
    function setAttestationAuthority(address _newAuthority) external onlyOwner {
        require(_newAuthority != address(0), "Invalid address");
        attestationAuthority = _newAuthority;
    }

    /**
     * @dev Update scholarship pool address
     */
    function setScholarshipPool(address _newPool) external onlyOwner {
        require(_newPool != address(0), "Invalid address");
        scholarshipPool = _newPool;
    }

    /**
     * @dev Update reward multiplier
     */
    function setRewardMultiplier(uint256 _newMultiplier) external onlyOwner {
        require(_newMultiplier >= 100 && _newMultiplier <= 200, "Invalid multiplier");
        rewardMultiplier = _newMultiplier;
    }

    /**
     * @dev Distribute scholarship from pool
     */
    function distributeScholarship(
        address _recipient,
        uint256 _amount
    ) external onlyOwner nonReentrant {
        require(_amount <= totalScholarshipFunds, "Insufficient scholarship funds");

        totalScholarshipFunds -= _amount;

        (bool success, ) = _recipient.call{value: _amount}("");
        require(success, "Scholarship distribution failed");

        emit ScholarshipDistributed(_recipient, _amount);
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ View Functions ============

    function getUserCommitments(address _user) external view returns (uint256[] memory) {
        return userCommitments[_user];
    }

    function getPodMembers(uint256 _podId) external view returns (address[] memory) {
        return pods[_podId].members;
    }

    function getCommitment(uint256 _commitmentId) external view returns (Commitment memory) {
        return commitments[_commitmentId];
    }

    function getPod(uint256 _podId) external view returns (Pod memory) {
        return pods[_podId];
    }

    // ============ Signature Helper Functions ============

    function getEthSignedMessageHash(bytes32 _messageHash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash));
    }

    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature)
        internal
        pure
        returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory _sig)
        internal
        pure
        returns (bytes32 r, bytes32 s, uint8 v)
    {
        require(_sig.length == 65, "Invalid signature length");

        assembly {
            r := mload(add(_sig, 32))
            s := mload(add(_sig, 64))
            v := byte(0, mload(add(_sig, 96)))
        }
    }

    // ============ Receive Function ============

    receive() external payable {
        // Accept donations to reward pool
    }
}

contracts_src/ReputationSBT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ReputationSBT
 * @dev Soulbound Token (non-transferable NFT) for tutor reputation
 * Tutors earn these badges for completing tutoring sessions
 * Cannot be transferred - they represent portable, verifiable reputation
 */
contract ReputationSBT is ERC721, Ownable {

    // ============ Structs ============

    struct Badge {
        uint256 tokenId;
        address tutor;
        uint256 sessionId;
        string badgeType;       // e.g., "essay_feedback", "speaking_practice"
        uint256 mintedAt;
        string metadataURI;
    }

    // ============ State Variables ============

    uint256 public nextTokenId = 1;
    address public escrowContract;

    mapping(uint256 => Badge) public badges;
    mapping(address => uint256[]) public tutorBadges;
    mapping(address => mapping(string => uint256)) public tutorBadgeCount; // tutor => badgeType => count

    // Badge type metadata
    mapping(string => string) public badgeTypeMetadata;

    // ============ Events ============

    event BadgeMinted(uint256 indexed tokenId, address indexed tutor, uint256 sessionId, string badgeType);
    event BadgeTypeAdded(string badgeType, string metadataURI);

    // ============ Constructor ============

    constructor() ERC721("EduLearn Tutor Reputation", "EDUREP") {
        // Initialize default badge types
        badgeTypeMetadata["essay_feedback"] = "ipfs://QmEssayFeedback";
        badgeTypeMetadata["speaking_practice"] = "ipfs://QmSpeakingPractice";
        badgeTypeMetadata["reading_tutor"] = "ipfs://QmReadingTutor";
        badgeTypeMetadata["writing_coach"] = "ipfs://QmWritingCoach";
    }

    // ============ Modifiers ============

    modifier onlyEscrow() {
        require(msg.sender == escrowContract, "Only escrow contract");
        _;
    }

    // ============ Main Functions ============

    /**
     * @dev Mint a reputation badge (called by escrow contract)
     * @param _tutor Address of the tutor
     * @param _sessionId Session ID from escrow
     */
    function mintReputation(address _tutor, uint256 _sessionId) external onlyEscrow {
        require(_tutor != address(0), "Invalid tutor address");

        uint256 tokenId = nextTokenId++;
        string memory badgeType = "essay_feedback"; // Default type for MVP

        // Mint the SBT
        _safeMint(_tutor, tokenId);

        // Store badge data
        badges[tokenId] = Badge({
            tokenId: tokenId,
            tutor: _tutor,
            sessionId: _sessionId,
            badgeType: badgeType,
            mintedAt: block.timestamp,
            metadataURI: badgeTypeMetadata[badgeType]
        });

        // Update tutor's badge collection
        tutorBadges[_tutor].push(tokenId);
        tutorBadgeCount[_tutor][badgeType]++;

        emit BadgeMinted(tokenId, _tutor, _sessionId, badgeType);
    }

    /**
     * @dev Mint a reputation badge with specific type (owner only)
     * @param _tutor Address of the tutor
     * @param _sessionId Session ID
     * @param _badgeType Type of badge
     */
    function mintReputationWithType(
        address _tutor,
        uint256 _sessionId,
        string memory _badgeType
    ) external onlyOwner {
        require(_tutor != address(0), "Invalid tutor address");
        require(bytes(badgeTypeMetadata[_badgeType]).length > 0, "Invalid badge type");

        uint256 tokenId = nextTokenId++;

        _safeMint(_tutor, tokenId);

        badges[tokenId] = Badge({
            tokenId: tokenId,
            tutor: _tutor,
            sessionId: _sessionId,
            badgeType: _badgeType,
            mintedAt: block.timestamp,
            metadataURI: badgeTypeMetadata[_badgeType]
        });

        tutorBadges[_tutor].push(tokenId);
        tutorBadgeCount[_tutor][_badgeType]++;

        emit BadgeMinted(tokenId, _tutor, _sessionId, _badgeType);
    }

    // ============ Soulbound Functions (Non-transferable) ============

    /**
     * @dev Override transfer functions to make tokens non-transferable
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal virtual override {
        require(from == address(0) || to == address(0), "Soulbound: Transfer not allowed");
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    /**
     * @dev Explicitly disable approvals for soulbound tokens
     */
    function approve(address /*to*/, uint256 /*tokenId*/) public virtual override {
        revert("Soulbound: Approvals not allowed");
    }

    /**
     * @dev Explicitly disable operator approvals
     */
    function setApprovalForAll(address /*operator*/, bool /*approved*/) public virtual override {
        revert("Soulbound: Approvals not allowed");
    }

    // ============ View Functions ============

    /**
     * @dev Get all badges for a tutor
     */
    function getTutorBadges(address _tutor) external view returns (uint256[] memory) {
        return tutorBadges[_tutor];
    }

    /**
     * @dev Get badge count by type for a tutor
     */
    function getTutorBadgeCount(address _tutor, string memory _badgeType) external view returns (uint256) {
        return tutorBadgeCount[_tutor][_badgeType];
    }

    /**
     * @dev Get total badges for a tutor
     */
    function getTotalBadges(address _tutor) external view returns (uint256) {
        return tutorBadges[_tutor].length;
    }

    /**
     * @dev Get badge details
     */
    function getBadge(uint256 _tokenId) external view returns (Badge memory) {
        require(_exists(_tokenId), "Badge does not exist");
        return badges[_tokenId];
    }

    /**
     * @dev Get tutor reputation summary
     */
    function getTutorReputation(address _tutor) external view returns (
        uint256 totalBadges,
        uint256 essayFeedback,
        uint256 speakingPractice,
        uint256 readingTutor,
        uint256 writingCoach
    ) {
        return (
            tutorBadges[_tutor].length,
            tutorBadgeCount[_tutor]["essay_feedback"],
            tutorBadgeCount[_tutor]["speaking_practice"],
            tutorBadgeCount[_tutor]["reading_tutor"],
            tutorBadgeCount[_tutor]["writing_coach"]
        );
    }

    /**
     * @dev Override tokenURI to return badge-specific metadata
     */
    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        require(_exists(_tokenId), "Token does not exist");
        return badges[_tokenId].metadataURI;
    }

    // ============ Admin Functions ============

    /**
     * @dev Set the escrow contract address
     */
    function setEscrowContract(address _escrow) external onlyOwner {
        require(_escrow != address(0), "Invalid address");
        escrowContract = _escrow;
    }

    /**
     * @dev Add or update badge type metadata
     */
    function setBadgeTypeMetadata(string memory _badgeType, string memory _metadataURI) external onlyOwner {
        require(bytes(_badgeType).length > 0, "Invalid badge type");
        require(bytes(_metadataURI).length > 0, "Invalid metadata URI");

        badgeTypeMetadata[_badgeType] = _metadataURI;

        emit BadgeTypeAdded(_badgeType, _metadataURI);
    }

    /**
     * @dev Burn a badge (owner only, in case of fraud)
     */
    function burnBadge(uint256 _tokenId) external onlyOwner {
        require(_exists(_tokenId), "Badge does not exist");

        Badge memory badge = badges[_tokenId];

        // Update counts
        tutorBadgeCount[badge.tutor][badge.badgeType]--;

        // Remove from tutor's badge array (note: leaves a gap, but maintains other indices)
        uint256[] storage tutorBadgeArray = tutorBadges[badge.tutor];
        for (uint256 i = 0; i < tutorBadgeArray.length; i++) {
            if (tutorBadgeArray[i] == _tokenId) {
                tutorBadgeArray[i] = tutorBadgeArray[tutorBadgeArray.length - 1];
                tutorBadgeArray.pop();
                break;
            }
        }

        // Delete badge data
        delete badges[_tokenId];

        // Burn the token
        _burn(_tokenId);
    }

    /**
     * @dev Check if token exists (helper for external calls)
     */
    function exists(uint256 _tokenId) external view returns (bool) {
        return _exists(_tokenId);
    }
}

contracts_src/ScholarshipPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title ScholarshipPool
 * @dev Quadratic funding-based scholarship pool for learners with verifiable improvement
 * Donors contribute to pool, learners receive rewards based on improvement attestations.
 * Uses quadratic funding to favor broad community support.
 */
contract ScholarshipPool is Ownable, ReentrancyGuard, Pausable {

    // ============ Structs ============

    struct Round {
        uint256 id;
        uint256 startTime;
        uint256 endTime;
        uint256 matchingPool;       // Platform matching funds
        uint256 totalDonations;     // Total community donations
        uint256 totalDistributed;   // Total rewards distributed
        bool finalized;
        uint256 learnerCount;       // Number of eligible learners
    }

    struct Donation {
        address donor;
        uint256 roundId;
        uint256 amount;
        uint256 timestamp;
    }

    struct ImprovementClaim {
        uint256 id;
        address learner;
        uint256 roundId;
        string metricType;          // e.g., "reading_score", "writing_score"
        uint256 beforeScore;
        uint256 afterScore;
        uint256 improvementPercent;
        uint256 timeframe;          // Days of improvement period
        bool verified;
        bool rewarded;
        uint256 rewardAmount;
        uint256 claimedAt;
    }

    struct LearnerStats {
        uint256 totalRewards;
        uint256 claimsCount;
        uint256 averageImprovement;
        uint256 donationsReceived;  // For QF calculation
    }

    // ============ State Variables ============

    uint256 public currentRoundId = 0;
    uint256 public nextClaimId = 1;
    address public attestationAuthority;

    // Quadratic Funding parameters
    uint256 public constant QF_MULTIPLIER = 100;  // Basis points for matching
    uint256 public minImprovementPercent = 10;    // Minimum 10% improvement required

    mapping(uint256 => Round) public rounds;
    mapping(uint256 => Donation[]) public roundDonations;
    mapping(uint256 => ImprovementClaim) public claims;
    mapping(uint256 => uint256[]) public roundClaims;  // roundId => claimIds[]
    mapping(address => LearnerStats) public learnerStats;
    mapping(address => mapping(uint256 => uint256)) public learnerRoundDonations;  // learner => roundId => donation count
    mapping(uint256 => address[]) public roundLearners;  // roundId => learner addresses

    // Donor tracking
    mapping(address => uint256) public totalDonatedByDonor;
    mapping(address => uint256[]) public donorDonations;  // donor => donation indices

    // ============ Events ============

    event RoundCreated(uint256 indexed roundId, uint256 startTime, uint256 endTime, uint256 matchingPool);
    event DonationMade(uint256 indexed roundId, address indexed donor, uint256 amount);
    event ImprovementClaimed(uint256 indexed claimId, address indexed learner, uint256 roundId, uint256 improvementPercent);
    event ClaimVerified(uint256 indexed claimId, address indexed learner, bool verified);
    event RewardDistributed(uint256 indexed claimId, address indexed learner, uint256 amount);
    event RoundFinalized(uint256 indexed roundId, uint256 totalDistributed);

    // ============ Constructor ============

    constructor(address _attestationAuthority) {
        require(_attestationAuthority != address(0), "Invalid attestation authority");
        attestationAuthority = _attestationAuthority;
    }

    // ============ Modifiers ============

    modifier onlyAttestationAuthority() {
        require(msg.sender == attestationAuthority, "Not attestation authority");
        _;
    }

    modifier roundActive(uint256 _roundId) {
        require(_roundId > 0 && _roundId <= currentRoundId, "Invalid round");
        require(block.timestamp >= rounds[_roundId].startTime, "Round not started");
        require(block.timestamp <= rounds[_roundId].endTime, "Round ended");
        require(!rounds[_roundId].finalized, "Round finalized");
        _;
    }

    // ============ Main Functions ============

    /**
     * @dev Create a new scholarship round
     * @param _duration Duration in seconds
     * @param _matchingPool Matching pool amount
     */
    function createRound(uint256 _duration, uint256 _matchingPool) external payable onlyOwner {
        require(_duration > 0, "Invalid duration");
        require(msg.value >= _matchingPool, "Insufficient matching pool");

        currentRoundId++;
        uint256 startTime = block.timestamp;
        uint256 endTime = startTime + _duration;

        rounds[currentRoundId] = Round({
            id: currentRoundId,
            startTime: startTime,
            endTime: endTime,
            matchingPool: _matchingPool,
            totalDonations: 0,
            totalDistributed: 0,
            finalized: false,
            learnerCount: 0
        });

        emit RoundCreated(currentRoundId, startTime, endTime, _matchingPool);
    }

    /**
     * @dev Donate to current round
     */
    function donate(uint256 _roundId) public payable roundActive(_roundId) nonReentrant {
        require(msg.value > 0, "Donation must be > 0");

        Round storage round = rounds[_roundId];
        round.totalDonations += msg.value;

        Donation memory donation = Donation({
            donor: msg.sender,
            roundId: _roundId,
            amount: msg.value,
            timestamp: block.timestamp
        });

        roundDonations[_roundId].push(donation);
        totalDonatedByDonor[msg.sender] += msg.value;
        donorDonations[msg.sender].push(roundDonations[_roundId].length - 1);

        emit DonationMade(_roundId, msg.sender, msg.value);
    }

    /**
     * @dev Learner claims improvement for reward eligibility
     * @param _roundId Round ID
     * @param _metricType Type of improvement (reading_score, writing_score, etc.)
     * @param _beforeScore Score before improvement period
     * @param _afterScore Score after improvement period
     * @param _timeframe Days of improvement period
     */
    function claimImprovement(
        uint256 _roundId,
        string memory _metricType,
        uint256 _beforeScore,
        uint256 _afterScore,
        uint256 _timeframe
    ) external roundActive(_roundId) {
        require(_beforeScore > 0, "Before score must be > 0");
        require(_afterScore > _beforeScore, "No improvement");
        require(_timeframe >= 7, "Minimum 7 days required");

        uint256 improvementPercent = ((_afterScore - _beforeScore) * 100) / _beforeScore;
        require(improvementPercent >= minImprovementPercent, "Insufficient improvement");

        uint256 claimId = nextClaimId++;

        claims[claimId] = ImprovementClaim({
            id: claimId,
            learner: msg.sender,
            roundId: _roundId,
            metricType: _metricType,
            beforeScore: _beforeScore,
            afterScore: _afterScore,
            improvementPercent: improvementPercent,
            timeframe: _timeframe,
            verified: false,
            rewarded: false,
            rewardAmount: 0,
            claimedAt: block.timestamp
        });

        roundClaims[_roundId].push(claimId);

        // Track learner for QF calculation
        if (learnerRoundDonations[msg.sender][_roundId] == 0) {
            roundLearners[_roundId].push(msg.sender);
            rounds[_roundId].learnerCount++;
        }
        learnerRoundDonations[msg.sender][_roundId]++;

        emit ImprovementClaimed(claimId, msg.sender, _roundId, improvementPercent);
    }

    /**
     * @dev Platform verifies improvement claim with attestation
     * @param _claimId Claim ID
     * @param _signature Platform attestation signature
     */
    function verifyImprovement(
        uint256 _claimId,
        bytes memory _signature
    ) external onlyAttestationAuthority {
        ImprovementClaim storage claim = claims[_claimId];
        require(!claim.verified, "Already verified");
        require(_signature.length > 0, "Invalid signature");

        claim.verified = true;

        // Update learner stats
        learnerStats[claim.learner].claimsCount++;
        learnerStats[claim.learner].averageImprovement =
            (learnerStats[claim.learner].averageImprovement * (learnerStats[claim.learner].claimsCount - 1) + claim.improvementPercent) /
            learnerStats[claim.learner].claimsCount;

        emit ClaimVerified(_claimId, claim.learner, true);
    }

    /**
     * @dev Finalize round and calculate QF rewards
     * @param _roundId Round ID to finalize
     */
    function finalizeRound(uint256 _roundId) external onlyOwner nonReentrant {
        Round storage round = rounds[_roundId];
        require(block.timestamp > round.endTime, "Round not ended");
        require(!round.finalized, "Already finalized");

        uint256[] memory claimIds = roundClaims[_roundId];
        require(claimIds.length > 0, "No claims");

        // Calculate QF allocations
        uint256 totalQFScore = 0;
        uint256[] memory qfScores = new uint256[](claimIds.length);

        // Calculate individual QF scores (simplified quadratic funding)
        for (uint256 i = 0; i < claimIds.length; i++) {
            ImprovementClaim storage claim = claims[claimIds[i]];
            if (claim.verified && !claim.rewarded) {
                // QF score = sqrt(donations) * improvement weight
                uint256 donationCount = learnerRoundDonations[claim.learner][_roundId];
                uint256 qfScore = sqrt(donationCount) * claim.improvementPercent;
                qfScores[i] = qfScore;
                totalQFScore += qfScore;
            }
        }

        // Distribute rewards based on QF scores
        uint256 availableFunds = round.matchingPool + round.totalDonations;

        for (uint256 i = 0; i < claimIds.length; i++) {
            ImprovementClaim storage claim = claims[claimIds[i]];
            if (claim.verified && !claim.rewarded && qfScores[i] > 0) {
                uint256 rewardAmount = (availableFunds * qfScores[i]) / totalQFScore;

                if (rewardAmount > 0) {
                    claim.rewarded = true;
                    claim.rewardAmount = rewardAmount;
                    round.totalDistributed += rewardAmount;

                    learnerStats[claim.learner].totalRewards += rewardAmount;
                    learnerStats[claim.learner].donationsReceived += rewardAmount;

                    // Transfer reward
                    (bool success, ) = payable(claim.learner).call{value: rewardAmount}("");
                    require(success, "Transfer failed");

                    emit RewardDistributed(claimIds[i], claim.learner, rewardAmount);
                }
            }
        }

        round.finalized = true;
        emit RoundFinalized(_roundId, round.totalDistributed);
    }

    // ============ View Functions ============

    function getCurrentRound() external view returns (Round memory) {
        return rounds[currentRoundId];
    }

    function getRound(uint256 _roundId) external view returns (Round memory) {
        return rounds[_roundId];
    }

    function getClaim(uint256 _claimId) external view returns (ImprovementClaim memory) {
        return claims[_claimId];
    }

    function getRoundClaims(uint256 _roundId) external view returns (uint256[] memory) {
        return roundClaims[_roundId];
    }

    function getRoundDonations(uint256 _roundId) external view returns (Donation[] memory) {
        return roundDonations[_roundId];
    }

    function getLearnerStats(address _learner) external view returns (LearnerStats memory) {
        return learnerStats[_learner];
    }

    function getRoundLearners(uint256 _roundId) external view returns (address[] memory) {
        return roundLearners[_roundId];
    }

    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // ============ Helper Functions ============

    /**
     * @dev Calculate square root (Babylonian method)
     */
    function sqrt(uint256 x) internal pure returns (uint256) {
        if (x == 0) return 0;
        uint256 z = (x + 1) / 2;
        uint256 y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
        return y;
    }

    // ============ Admin Functions ============

    function setAttestationAuthority(address _authority) external onlyOwner {
        require(_authority != address(0), "Invalid address");
        attestationAuthority = _authority;
    }

    function setMinImprovementPercent(uint256 _percent) external onlyOwner {
        require(_percent > 0 && _percent <= 100, "Invalid percentage");
        minImprovementPercent = _percent;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Emergency withdraw (only if paused and no active rounds)
    function emergencyWithdraw() external onlyOwner {
        require(paused(), "Not paused");
        if (currentRoundId > 0) {
            require(rounds[currentRoundId].finalized, "Active round exists");
        }

        (bool success, ) = payable(owner()).call{value: address(this).balance}("");
        require(success, "Withdrawal failed");
    }

    // Accept ETH
    receive() external payable {
        if (currentRoundId > 0 && block.timestamp <= rounds[currentRoundId].endTime) {
            donate(currentRoundId);
        }
    }
}

